        private const string AtomCharacters = "!#$%&'*+-/=?^_`{|}~";
        private static bool IsLetterOrDigit(char c)
        {
            return (c >= 'A' && c <= 'Z') || (c >= 'a' && c <= 'z') || (c >= '0' && c <= '9');
