            where TEnum : struct
        {
            static CachedEnumValidator()
            {
                EnumType = typeof(TEnum);
                HasFlagsAttribute = TypeHasFlagsAttribute(EnumType);
                if (HasFlagsAttribute)
                {
                    var mask = 0;
                    foreach (var enumValue in Enum.GetValues(EnumType))
                    {
